import React from "react";
import './styles.css';

function Nav() {

    // window.addEventListener("scroll", function() {
    //     let elementTarget = this.document.getElementById("header");

    //     if (this.window.scrollY > (elementTarget.offsetTop + elementTarget.offsetHeight)) {
    //         document.getElementById("navbar").style.top = "0";
    //     } else {
    //       document.getElementById("navbar").style.top = "-50px";
    //     }
    // })    

    const toggle = document.querySelector(".toggle");
const menu = document.querySelector(".menu");

/* Toggle mobile menu */
function toggleMenu() {
    if (menu.classList.contains("active")) {
        menu.classList.remove("active");

        // adds the menu (hamburger) icon
        toggle.querySelector("a").innerHTML = "<i class=’fas fa-bars’></i>";
    } else {
        menu.classList.add("active");

        // adds the close (x) icon
        toggle.querySelector("a").innerHTML = "<i class=’fas fa-times’></i>";
    }
}

/* Event Listener */
toggle.addEventListener("click", toggleMenu, false);

return(
    <nav>
        <ul className="menu">
            <li className="item"><a href="#">Dark Sky</a></li>
            <li className="item"><a href="#">World Map</a></li>
            <li className="item"><a href="#">About</a></li>
            <li className="item button"><a href="#">Log In</a></li>
            <li className="item button secondary"><a href="#">Sign Up</a></li>
            <li className="toggle"><a href="#"><i className="fas fa-bars"></i></a></li>
        </ul>
    </nav>
    );
}

export default Nav;
